import React from "react";
import "./backgroundvideo.css"; // Import the CSS file
import  { useState, useEffect, useMemo } from "react";

const BackgroundVideo = () => {

  
   // FIX: Wrap 'messages' initialization in useMemo
  const messages = useMemo(() => {
    return ["Welcome Back Nyambuu"];
  }, []); // <-- Empty dependency array means it's only created once

  const [currentText, setCurrentText] = useState("");
  const [index, setIndex] = useState(0);
  const [charIndex, setCharIndex] = useState(0);

  useEffect(() => {
    const timeout = setTimeout(() => {
      if (charIndex < messages[index].length) {
        setCurrentText(prev => prev + messages[index][charIndex]);
        setCharIndex(prev => prev + 1);
      } else {
        setTimeout(() => {
          setCurrentText("");
          setCharIndex(0);
          setIndex((index + 1) % messages.length);
        }, 2000);
      }
    }, 100);
     
      return () => clearTimeout(timeout);
  }, [charIndex, index, messages]); // 'messages' is now stable, so this dependency is fine.
    

  return (
    <div className="video-container">
      <video autoPlay muted loop id="background-video">
        <source src="/media/football.mp4" type="video/mp4" />
        Your browser does not support the video tag.
      </video>
      <div className="content">
      {currentText}
    </div>
    </div>
  );
};

export default BackgroundVideo;
