import React, { useState, useEffect, useMemo } from "react";
import "../Styles/Home.css";
import NavBar from "../pages/Navbar";
import backgroundImage from "../images/img2.jpg"; // Import image
import { useNavigate } from "react-router-dom"; // for redirect after login

export default function LoginPage() {
  // Typewriter effect
  const messages = useMemo(
    () => ["Welcome to the Football Registration Portal! Please log in below."],
    []
  );

  const [currentText, setCurrentText] = useState("");
  const [index, setIndex] = useState(0);
  const [charIndex, setCharIndex] = useState(0);

  // Login form state
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const timeout = setTimeout(() => {
      if (charIndex < messages[index].length) {
        setCurrentText(prev => prev + messages[index][charIndex]);
        setCharIndex(prev => prev + 1);
      } else {
        setTimeout(() => {
          setCurrentText("");
          setCharIndex(0);
          setIndex((index + 1) % messages.length);
        }, 2000);
      }
    }, 100);

    return () => clearTimeout(timeout);
  }, [charIndex, index, messages]);

  // Handle login submit
  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");

    try {
      const res = await fetch("/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password }),
      });

      const data = await res.json();
      if (res.ok) {
        // Save token & user info
        localStorage.setItem("token", data.token);
        localStorage.setItem("username", data.username);
        localStorage.setItem("teamName", data.teamName);
        // Redirect to team page or dashboard
        navigate("/dashboard"); // change to your route
      } else {
        setError(data.message || "Login failed");
      }
    } catch (err) {
      console.error(err);
      setError("Server error. Please try again later.");
    }
  };

  return (
    <div
      className="home-container"
      style={{
        backgroundImage: `url(${backgroundImage})`,
      }}
    >
      <NavBar />
      <div className="overlay">
        <div className="message-text">{currentText}</div>

      </div>
    </div>
  );
}
