import React from "react";
import "./backgroundvideo.css"; // Import the CSS file
import  { useState, useEffect, useMemo } from "react";

const BackgroundVideo = () => {

   // Messages to display (same as in your screenshot)
  const messages = useMemo(
    () => ["Welcome to Football Registration System"],
    []
  );

  const [currentText, setCurrentText] = useState("");
  const [index, setIndex] = useState(0); // Which message we are showing
  const [charIndex, setCharIndex] = useState(0); // Letter typing position

  useEffect(() => {
    const typingInterval = setInterval(() => {
      if (charIndex < messages[index].length) {
        // Add next letter
        setCurrentText((prev) => prev + messages[index][charIndex]);
        setCharIndex((prev) => prev + 1);
      } else {
        // Move to next message after a pause
        setTimeout(() => {
          setCharIndex(0);
          setCurrentText("");
          setIndex((prev) => (prev + 1) % messages.length);
        }, 5000);
      }
    }, 400);

    return () => clearInterval(typingInterval);
  }, [charIndex, index, messages]);

  return (
    <div className="video-container">
      <video autoPlay muted loop id="background-video">
        <source src="/media/football.mp4" type="video/mp4" />
        Your browser does not support the video tag.
      </video>
      <div className="content">
      {currentText}
    </div>
    </div>
  );
};

export default BackgroundVideo;
